fun uploadFile(fileName: String, filePath: String, remotePath: String, callBack: NewOssCallback) {
        this.ossCallback = callBack
        this.remotePath = "${UserUtils.getUserInfo().userId}/${remotePath}"
        GlobalScope.launch {
            val result = repository.getOSSToken(Empty)
            if (result.isSuccess) {
                result.data?.data?.let {
                    catchException("Get Oss Params") {
                        ossParamsHandler(fileName,filePath,it)
                    }
                }
            }
        }
    }

    private fun ossParamsHandler(fileName:String, filePath:String,ossParamsResponse: OssParamsResponse) {
        Log.e("tag","fileName = $fileName ossParamsResponse ${ossParamsResponse.access_key_id} ,${ossParamsResponse.access_key_secret} ,${ossParamsResponse.security_token}")

        if(ossParamsResponse.access_key_id.isNullOrEmpty() || ossParamsResponse.access_key_secret.isNullOrEmpty() ||ossParamsResponse.security_token.isNullOrEmpty()){
            ossCallback?.onFailed("连接上传失败",fileName)
            return
        }
        val credentialProvider = OSSStsTokenCredentialProvider(ossParamsResponse.access_key_id, ossParamsResponse.access_key_secret, ossParamsResponse.security_token)
        val conf = ClientConfiguration()
        conf.connectionTimeout = 30 * 1000 // 连接超时，默认15秒
        conf.socketTimeout = 30 * 1000 // socket超时，默认15秒
        conf.maxConcurrentRequest = 9 // 最大并发请求书，默认5个
        conf.maxErrorRetry = 2 // 失败后最大重试次数，默认2次

        var oss = OSSClient(context, ossParamsResponse.endpoint, credentialProvider, conf)

        val objectKey = "$remotePath/$fileName"
        // 构造上传请求
        val put = PutObjectRequest(ossParamsResponse.bucket_name, objectKey, filePath)
        // 异步上传时可以设置进度回调
        put.progressCallback = OSSProgressCallback<PutObjectRequest?> { request, currentSize, totalSize ->
            Log.e("progress","onProgressChanged size = $currentSize total $totalSize  "+currentSize / (totalSize.toFloat()))
            ossCallback?.onProgressChanged(currentSize / totalSize.toFloat(),fileName)
        }

        var task = oss.asyncPutObject(put,object :OSSCompletedCallback<PutObjectRequest, PutObjectResult>{
            override fun onSuccess(p0: PutObjectRequest?, p1: PutObjectResult?) {
                Log.e("progress","onSuccess")
                ossCallback?.onSuccess(ossParamsResponse.cdn_url + "/" + objectKey,fileName)
            }

            override fun onFailure(p0: PutObjectRequest?, p1: ClientException?, p2: ServiceException?) {
                Log.e("progress","onFailed")
                ossCallback?.onFailed("网络出错啦",fileName)
            }
        })
        task?.let { taskMap.put(fileName,it) }
    }
